import React, {useEffect, useState} from 'react';
import {getWeb3, getWallet} from './utils.js'

function App() {

  const [web3, setWeb3] = useState(undefined);
  const [accounts, setAccounts] = useState(undefined);
  const [wallet, setWallet] = useState(undefined);

  useEffect(() => {
    const init = async () =>{
      setWeb3(getWeb3());
      setWallet(await getWallet(web3));
      setAccounts(web3.eth.getAccounts());
    };
    init();

    return () => {
      // cleanup
    }
  }, []);

  if(typeof web3 == undefined || typeof wallet == undefined || typeof accounts == undefined)
  {
    return(
      <div>Loading...</div>
    );
  }

  return (
    <div className="App">
      Multi Sig app
    </div>
  );
}

export default App;
